mapper.update:
INSERT INTO solarev.ocpp_charge_point_conn (
	cp_id
	, conn_id
	,created
	, status
	, error_code
	, ts
	, info
	, vendor_id
	, vendor_error
) VALUES (
	#{id.chargePointId}
	, #{id.connectorId}
	,#{created}
	, #{info.status,typeHandler=net.solarnetwork.central.dao.mybatis.type.CodedValueTypeHandler}
	, #{info.errorCode,typeHandler=net.solarnetwork.central.dao.mybatis.type.CodedValueTypeHandler}
	, #{info.timestamp}
	, #{info.info}
	, #{info.vendorId}
	, #{info.vendorErrorCode}
)
ON CONFLICT (cp_id, conn_id) DO UPDATE
SET status = EXCLUDED.status
	, error_code = EXCLUDED.error_code
	, ts = EXCLUDED.ts
	, info = EXCLUDED.info
	, vendor_id = EXCLUDED.vendor_id
	, vendor_error = EXCLUDED.vendor_error

UPDATE
	solarev.ocpp_charge_point_conn
SET
	status = #{info.status,typeHandler=net.solarnetwork.central.dao.mybatis.type.CodedValueTypeHandler}
WHERE
	cp_id = #{id.chargePointId}
	AND conn_id = #{id.connectorId}

mapper.select:
SELECT

FROM
	solarev.ocpp_charge_point_conn cc
WHERE
	cc.cp_id = #{chargePointId}
	AND cc.conn_id = #{connectorId}
