mapper.update:
INSERT INTO solarev.ocpp_user_settings (
	user_id
	,created
	, source_id_tmpl
	, pub_in
	, pub_flux
) VALUES (
	#{id}
	,#{created}
	, #{sourceIdTemplate}
	, #{publishToSolarIn}
	, #{publishToSolarFlux}
)
ON CONFLICT (user_id) DO UPDATE
SET
	source_id_tmpl = EXCLUDED.source_id_tmpl
	, pub_in = EXCLUDED.pub_in
	, pub_flux = EXCLUDED.pub_flux
