mapper.insert:

              SELECT nextval('solaruser.user_export_seq') AS id 

		INSERT INTO solaruser.user_export_datum_conf (
			id, user_id, cname, delay_mins, schedule, data_conf_id, dest_conf_id, outp_conf_id
			,created
			,min_export_date
			)
		VALUES (
			#{id}, #{userId}, #{name}, (#{hourDelayOffset} * 60), #{scheduleKey},
			#{userDataConfigurationId}, #{userDestinationConfigurationId}, #{userOutputConfigurationId}
			,#{created}
			,#{minimumExportDate}
			)

mapper.update:
UPDATE
	solaruser.user_export_datum_conf
SET
	cname = #{name}
	, delay_mins = (#{hourDelayOffset} * 60)
	, schedule = #{scheduleKey}
	, data_conf_id = #{userDataConfigurationId}
	, dest_conf_id = #{userDestinationConfigurationId}
	, outp_conf_id = #{userOutputConfigurationId}
	, min_export_date = #{minimumExportDate}
WHERE
	id = #{id} AND user_id = #{userId}

UPDATE
	solaruser.user_export_datum_conf
SET
	min_export_date = #{date}
WHERE
	id = #{id} 
	AND user_id = #{userId} 
	AND (min_export_date IS NULL OR min_export_date < #{date})

mapper.select:

WHERE
	udexc.schedule = #{schedule}
	AND udexc.min_export_date < #{date}
ORDER BY
	udexc.min_export_date, udexc.id
