mapper.sql:
FROM solarbill.bill_invoice inv INNER JOIN solarbill.bill_account acct ON acct.id = inv.acct_id INNER JOIN solarbill.bill_address addr ON addr.id = inv.addr_id LEFT OUTER JOIN solarbill.bill_invoice_item invi ON invi.inv_id = inv.id LEFT OUTER JOIN solarbill.bill_invoice_node_usage invu ON invu.inv_id = inv.id LEFT OUTER JOIN solarnet.sn_node n ON n.node_id = invu.node_id
INNER JOIN LATERAL ( WITH paid AS ( SELECT SUM(ipay1.amount) AS paid FROM solarbill.bill_invoice_payment ipay1 WHERE ipay1.inv_id = inv.id AND ipay1.acct_id = inv.acct_id ) , bill AS ( SELECT SUM(invi1.amount) AS bill FROM solarbill.bill_invoice_item invi1 WHERE invi1.inv_id = inv.id ) SELECT inv.id AS inv_id FROM paid, bill WHERE COALESCE(paid, 0::NUMERIC) < COALESCE(bill, 0::NUMERIC) ) pay ON pay.inv_id = inv.id
INNER JOIN LATERAL ( SELECT inv.id AS inv_id FROM solarbill.bill_invoice_item invi1 WHERE invi1.inv_id = inv.id AND NOT EXISTS( SELECT invi1.inv_id FROM solarbill.bill_invoice_item invi1 WHERE invi1.inv_id = inv.id AND invi1.item_key = 'account-credit-add' ) ) noncred ON noncred.inv_id = inv.id
AND inv.id = ANY( SELECT inv1.id FROM solarbill.bill_invoice inv1
INNER JOIN solarbill.bill_account acct1 ON acct.id = inv1.acct_id
INNER JOIN LATERAL ( WITH paid AS ( SELECT SUM(ipay1.amount) AS paid FROM solarbill.bill_invoice_payment ipay1 WHERE ipay1.inv_id = inv.id AND ipay1.acct_id = inv.acct_id ) , bill AS ( SELECT SUM(invi1.amount) AS bill FROM solarbill.bill_invoice_item invi1 WHERE invi1.inv_id = inv.id ) SELECT inv.id AS inv_id FROM paid, bill WHERE COALESCE(paid, 0::NUMERIC) < COALESCE(bill, 0::NUMERIC) ) pay ON pay.inv_id = inv.id
AND acct1.user_id = #{userId}
AND inv1.acct_id = #{accountId}
AND inv1.date_start >= #{startDate,javaType=java.time.LocalDate,jdbcType=DATE}
AND inv1.date_start < #{endDate,javaType=java.time.LocalDate,jdbcType=DATE}
AND NOT EXISTS( SELECT invi1.inv_id FROM solarbill.bill_invoice_item invi1 WHERE invi1.inv_id = inv1.id AND invi1.item_key = 'account-credit-add' )
ORDER BY
inv1.acct_id inv1.date_start inv1.id DESC
inv1.date_start DESC, inv1.id
LIMIT #{max} OFFSET #{offset} )
AND acct.user_id = #{userId}
AND inv.acct_id = #{accountId}
AND inv.date_start >= #{startDate,javaType=java.time.LocalDate,jdbcType=DATE}
AND inv.date_start < #{endDate,javaType=java.time.LocalDate,jdbcType=DATE}
