mapper.insert:

              SELECT nextval('solaruser.user_export_seq') AS id 

		INSERT INTO solaruser.user_export_data_conf (
			id, user_id, cname, sident
			,created
			,sprops
			,filter
			)
		VALUES (
			#{id}, #{userId}, #{name}, #{serviceIdentifier}
			,#{created}
			,CAST(#{servicePropsJson} AS jsonb)
			,CAST(#{filterJson} AS jsonb)
			)

mapper.update:
UPDATE
	solaruser.user_export_data_conf
SET
	cname = #{name}
	, sident = #{serviceIdentifier}
	, sprops = CAST(#{servicePropsJson} AS jsonb)
	, filter = CAST(#{filterJson} AS jsonb)
WHERE
	id = #{id} AND user_id = #{userId}
